mapper.select:
SELECT loan_id, loan_name, loan_content, loan_division, loan_category, interestRate_category, creditLine_desc, loanPeriod_desc, repayment_way, detail_url, target_description, TO_CHAR(loanUpdate_date, 'MM-DD-YYYY') "UPDATE_DATE", target_detail1, target_detail2, target_detail3, holding, holding_min, holding_max, repay1, repay2, repay3, repayM, t.target_name, c.loancomp_name, c.present_num, c.sub_num, loanperiod_min, loanperiod_max FROM loan_product p JOIN loan_target t ON p.target_id = t.target_id JOIN loan_company c ON p.loanComp_id = c.loanComp_id
WHERE loan_category = '신용' AND #{requiredMoney} BETWEEN creditLine_min AND creditLine_max AND #{resultRate} BETWEEN interestRate_min AND interestRate_max AND t.target_id = #{targetNum}
AND term_service >= #{termService}
AND rownum < 6
WHERE loan_category = '담보'
AND creditLine_max >= #{requiredMoney}
AND t.target_id = #{targetNum}
AND security_type = #{securityType}
AND term_service >= #{termService}
AND rownum < 6
ORDER BY interestRate_min
